import React from 'react'
import { Link } from 'react-router-dom'

let getTitle = (note) => {
  let title = note.body.split('/n')[0]
  if(title.length>45){
    return title.slice(0,45)
  }
  return title
}

const Listitem = ({note}) => {
  return (
    <div>
        <Link to={`/note/${note.id}`}>
          <div className="notes-list-item">
            <h1>{getTitle(note)}</h1>
          </div>
        </Link>
    </div>
  )
}

export default Listitem
